import { useState, useEffect } from "react";
import './App.css';

function App() {
  // set random color
  const [color, setColor] = useState("");
  const [answers, setAnswers] = useState([]);
  const [isWrongGuess, setIsWrongGuess] = useState(false);
  // const [wrongColor1, setWrongColor1] = useState("");
  // const [wrongColor2, setWrongColor2] = useState("");

  const getRandomColor = () => {
    const randomColor = Math.floor(Math.random() * 0xffffff).toString(16);
    return "#" + randomColor.padStart(6, '0');
  }

  const selectColors = () => {
    const actualColor = getRandomColor();
    setColor(actualColor);
    setAnswers([actualColor, getRandomColor(), getRandomColor()].sort(() => 0.5 - Math.random()));
  }

  useEffect(() => {
    selectColors();
  }, [])

  const handleButtonClick = (answer) => {
    if (answer === color) {
      setIsWrongGuess(false);
      selectColors();
    } else {
      setIsWrongGuess(true);
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="guesses">
          <p>
            Guess what color this is.
          </p>
          <div className="color-box" style={{background: color}}></div>
          <div className="flex-container">
            {answers.map(answer => (
              <button className="hex-button" key={answer} onClick={() => handleButtonClick(answer)}>{answer}</button>
            ))}
          </div>
        </div>
        <p className="wrong-guess" style={{ visibility: isWrongGuess ? 'visible' : 'hidden' }}>
          Wrong guess.
        </p>
        <p className="wrong-guess" style={{ visibility: isWrongGuess ? 'visible' : 'hidden' }}>
          Wrong guess.
        </p>
      </header>
    </div>
  );
}

export default App;
