import { useState, useEffect } from "react";
import './App.css';

function App() {
  // set random color
  const [color, setColor] = useState("");
  const [answers, setAnswers] = useState([]);
  // const [wrongColor1, setWrongColor1] = useState("");
  // const [wrongColor2, setWrongColor2] = useState("");

  const getRandomColor = () => {
    const randomColor = Math.floor(Math.random() * 0xffffff).toString(16);
    return "#" + randomColor.padStart(6, '0');
  }

  useEffect(() => {
    // generate random color
    // setColor("#61dafb");
    // getRandomColor();
    setColor(getRandomColor());
    setAnswers([color, getRandomColor(), getRandomColor].sort(() => Math.random() > 0.5))
    // setWrongColor1(getRandomColor());
    // setWrongColor2(getRandomColor());
  }, [])

  const wrongButtonClick = () => {

  }

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Guess what color this is.
        </p>
        <div className="color-box" style={{background: color}}></div>
        <div className="flex-container">
          <button className="hex-button" id={answers[0]}>{answers[0]}</button>
          <button className="hex-button" id={answers[1]}>{answers[1]}</button>
          <button className="hex-button" id={answers[2]}>{answers[2]}</button>
          {/* <button className="hex-button" id={wrongColor1}>{wrongColor1}</button>
          <button className="hex-button" id={wrongColor2}>{wrongColor2}</button> */}
        </div>
        <p className="wrong-guess">
          Wrong guess.
        </p>
      </header>
    </div>
  );
}

export default App;
